contracts/CarbonCredits.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title CarbonCredits
 * @dev ERC20 token representing carbon credits in the PowerChain ecosystem
 * @notice This contract manages carbon credits (CC) earned through renewable energy trading
 */
contract CarbonCredits is ERC20, ERC20Burnable, Ownable, Pausable, ReentrancyGuard {

    // Events
    event CarbonCreditsEarned(address indexed user, uint256 amount, uint256 energyAmount, string energyType);
    event CarbonCreditsRetired(address indexed user, uint256 amount, string reason);
    event CarbonCreditsTransferred(address indexed from, address indexed to, uint256 amount);

    // Constants
    uint256 public constant INITIAL_SUPPLY = 0; // No initial supply - only earned
    uint256 public constant MAX_SUPPLY = 50000000 * 10**18; // 50 million CC max

    // Carbon credit rates per kWh of different energy types (in CC tokens * 10^18)
    mapping(string => uint256) public carbonCreditRates;

    // Track carbon credit history
    struct CarbonRecord {
        uint256 amount;
        uint256 timestamp;
        string energyType;
        uint256 energyAmount;
        bool retired;
    }

    mapping(address => CarbonRecord[]) public carbonHistory;
    mapping(address => uint256) public totalCarbonEarned;
    mapping(address => uint256) public totalCarbonRetired;

    // Energy trading contract addresses (authorized to issue credits)
    mapping(address => bool) public authorizedIssuers;

    modifier onlyAuthorizedIssuer() {
        require(authorizedIssuers[msg.sender] || msg.sender == owner(), "Not authorized to issue credits");
        _;
    }

    constructor() ERC20("PowerChain Carbon Credits", "CC") Ownable(msg.sender) {
        // Set initial carbon credit rates (CC per kWh)
        carbonCreditRates["solar"] = 50 * 10**15;    // 0.05 CC per kWh
        carbonCreditRates["wind"] = 45 * 10**15;     // 0.045 CC per kWh
        carbonCreditRates["hydro"] = 40 * 10**15;    // 0.04 CC per kWh
        carbonCreditRates["storage"] = 30 * 10**15;  // 0.03 CC per kWh (battery storage)

        authorizedIssuers[msg.sender] = true;
    }

    /**
     * @dev Pause the contract (only owner)
     */
    function pause() public onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause the contract (only owner)
     */
    function unpause() public onlyOwner {
        _unpause();
    }

    /**
     * @dev Add authorized issuer
     */
    function addIssuer(address _issuer) public onlyOwner {
        authorizedIssuers[_issuer] = true;
    }

    /**
     * @dev Remove authorized issuer
     */
    function removeIssuer(address _issuer) public onlyOwner {
        authorizedIssuers[_issuer] = false;
    }

    /**
     * @dev Set carbon credit rate for energy type
     * @param _energyType Type of energy (solar, wind, hydro, storage)
     * @param _rate Rate in CC tokens per kWh (in wei)
     */
    function setCarbonCreditRate(string memory _energyType, uint256 _rate) public onlyOwner {
        require(_rate > 0, "Rate must be positive");
        carbonCreditRates[_energyType] = _rate;
    }

    /**
     * @dev Issue carbon credits for renewable energy production/trading
     * @param _to Address to receive credits
     * @param _energyAmount Amount of energy in kWh (in wei)
     * @param _energyType Type of renewable energy
     */
    function issueCarbonCredits(
        address _to,
        uint256 _energyAmount,
        string memory _energyType
    ) public onlyAuthorizedIssuer nonReentrant {
        require(_energyAmount > 0, "Energy amount must be positive");
        require(carbonCreditRates[_energyType] > 0, "Energy type not supported");

        uint256 creditsToIssue = (_energyAmount * carbonCreditRates[_energyType]) / 10**18;
        require(totalSupply() + creditsToIssue <= MAX_SUPPLY, "Would exceed max supply");

        _mint(_to, creditsToIssue);

        // Record carbon credit history
        carbonHistory[_to].push(CarbonRecord({
            amount: creditsToIssue,
            timestamp: block.timestamp,
            energyType: _energyType,
            energyAmount: _energyAmount,
            retired: false
        }));

        totalCarbonEarned[_to] += creditsToIssue;

        emit CarbonCreditsEarned(_to, creditsToIssue, _energyAmount, _energyType);
    }

    /**
     * @dev Retire carbon credits (remove from circulation)
     * @param _amount Amount of credits to retire
     * @param _reason Reason for retirement
     */
    function retireCarbonCredits(uint256 _amount, string memory _reason) public nonReentrant {
        require(_amount > 0, "Amount must be positive");
        require(balanceOf(msg.sender) >= _amount, "Insufficient credits");

        _burn(msg.sender, _amount);
        totalCarbonRetired[msg.sender] += _amount;

        emit CarbonCreditsRetired(msg.sender, _amount, _reason);
    }

    /**
     * @dev Transfer carbon credits (override to add tracking)
     */
    function transfer(address _to, uint256 _amount) public override returns (bool) {
        bool success = super.transfer(_to, _amount);
        if (success) {
            emit CarbonCreditsTransferred(msg.sender, _to, _amount);
        }
        return success;
    }

    /**
     * @dev Transfer carbon credits from (override to add tracking)
     */
    function transferFrom(address _from, address _to, uint256 _amount) public override returns (bool) {
        bool success = super.transferFrom(_from, _to, _amount);
        if (success) {
            emit CarbonCreditsTransferred(_from, _to, _amount);
        }
        return success;
    }

    /**
     * @dev Get carbon credit history for an address
     */
    function getCarbonHistory(address _user) public view returns (CarbonRecord[] memory) {
        return carbonHistory[_user];
    }

    /**
     * @dev Get carbon credit statistics for an address
     */
    function getCarbonStats(address _user) public view returns (
        uint256 totalEarned,
        uint256 totalRetired,
        uint256 currentBalance,
        uint256 recordCount
    ) {
        return (
            totalCarbonEarned[_user],
            totalCarbonRetired[_user],
            balanceOf(_user),
            carbonHistory[_user].length
        );
    }

    /**
     * @dev Calculate carbon credits for energy amount and type
     */
    function calculateCarbonCredits(uint256 _energyAmount, string memory _energyType) 
        public view returns (uint256) {
        require(carbonCreditRates[_energyType] > 0, "Energy type not supported");
        return (_energyAmount * carbonCreditRates[_energyType]) / 10**18;
    }

    /**
     * @dev Get carbon credit rate for energy type
     */
    function getCarbonCreditRate(string memory _energyType) public view returns (uint256) {
        return carbonCreditRates[_energyType];
    }

    /**
     * @dev Get total carbon credits in circulation
     */
    function getTotalCarbonCredits() public view returns (uint256) {
        return totalSupply();
    }

    /**
     * @dev Get environmental impact metrics
     */
    function getEnvironmentalImpact() public view returns (
        uint256 totalCreditsIssued,
        uint256 totalCreditsRetired,
        uint256 netEnvironmentalImpact
    ) {
        uint256 totalIssued = totalSupply() + getTotalRetired();
        uint256 totalRetired = getTotalRetired();

        return (
            totalIssued,
            totalRetired,
            totalIssued - totalRetired
        );
    }

    /**
     * @dev Get total retired carbon credits across all users
     */
    function getTotalRetired() public view returns (uint256) {
        // This would need to be tracked globally in a real implementation
        // For now, we'll return a calculated value
        return 0; // Placeholder - implement global tracking if needed
    }

    // Override required functions for Pausable
    function _update(
        address from,
        address to,
        uint256 value
    ) internal override whenNotPaused {
        super._update(from, to, value);
    }
}

contracts/EnergyCredits.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title EnergyCredits
 * @dev ERC20 token representing energy credits in the PowerChain ecosystem
 * @notice This contract manages the energy credits (EC) used for trading renewable energy
 */
contract EnergyCredits is ERC20, ERC20Burnable, Ownable, Pausable, ReentrancyGuard {

    // Events
    event EnergyPurchased(address indexed buyer, address indexed seller, uint256 amount, uint256 energyAmount);
    event EnergyCreditsIssued(address indexed to, uint256 amount, string reason);
    event EnergyCreditsRedeemed(address indexed from, uint256 amount, uint256 energyAmount);

    // Constants
    uint256 public constant INITIAL_SUPPLY = 1000000 * 10**18; // 1 million EC
    uint256 public constant MAX_SUPPLY = 100000000 * 10**18; // 100 million EC max

    // State variables
    mapping(address => bool) public authorizedMinters;
    mapping(address => uint256) public energyBalance; // Track actual energy in kWh

    // Energy trading parameters
    uint256 public energyToTokenRatio = 100; // 1 kWh = 100 EC (adjustable)
    uint256 public minimumEnergyAmount = 1 * 10**18; // Minimum 1 kWh in wei

    modifier onlyAuthorizedMinter() {
        require(authorizedMinters[msg.sender] || msg.sender == owner(), "Not authorized to mint");
        _;
    }

    constructor() ERC20("PowerChain Energy Credits", "EC") Ownable(msg.sender) {
        _mint(msg.sender, INITIAL_SUPPLY);
        authorizedMinters[msg.sender] = true;
    }

    /**
     * @dev Pause the contract (only owner)
     */
    function pause() public onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause the contract (only owner)
     */
    function unpause() public onlyOwner {
        _unpause();
    }

    /**
     * @dev Add authorized minter
     */
    function addMinter(address _minter) public onlyOwner {
        authorizedMinters[_minter] = true;
    }

    /**
     * @dev Remove authorized minter
     */
    function removeMinter(address _minter) public onlyOwner {
        authorizedMinters[_minter] = false;
    }

    /**
     * @dev Set energy to token conversion ratio
     * @param _ratio New ratio (e.g., 100 means 1 kWh = 100 EC)
     */
    function setEnergyToTokenRatio(uint256 _ratio) public onlyOwner {
        require(_ratio > 0, "Ratio must be positive");
        energyToTokenRatio = _ratio;
    }

    /**
     * @dev Issue energy credits for renewable energy production
     * @param _to Address to receive credits
     * @param _energyAmount Amount of energy in kWh (in wei)
     * @param _reason Reason for issuance
     */
    function issueEnergyCredits(
        address _to,
        uint256 _energyAmount,
        string memory _reason
    ) public onlyAuthorizedMinter nonReentrant {
        require(_energyAmount >= minimumEnergyAmount, "Energy amount too small");

        uint256 creditsToIssue = (_energyAmount * energyToTokenRatio) / 10**18;
        require(totalSupply() + creditsToIssue <= MAX_SUPPLY, "Would exceed max supply");

        _mint(_to, creditsToIssue);
        energyBalance[_to] += _energyAmount;

        emit EnergyCreditsIssued(_to, creditsToIssue, _reason);
    }

    /**
     * @dev Redeem energy credits for actual energy consumption
     * @param _energyAmount Amount of energy to redeem in kWh (in wei)
     */
    function redeemEnergyCredits(uint256 _energyAmount) public nonReentrant {
        require(_energyAmount >= minimumEnergyAmount, "Energy amount too small");
        require(energyBalance[msg.sender] >= _energyAmount, "Insufficient energy balance");

        uint256 creditsToRedeem = (_energyAmount * energyToTokenRatio) / 10**18;
        require(balanceOf(msg.sender) >= creditsToRedeem, "Insufficient credits");

        _burn(msg.sender, creditsToRedeem);
        energyBalance[msg.sender] -= _energyAmount;

        emit EnergyCreditsRedeemed(msg.sender, creditsToRedeem, _energyAmount);
    }

    /**
     * @dev Transfer energy credits between users (for trading)
     * @param _to Recipient address
     * @param _amount Amount of credits to transfer
     * @param _energyAmount Corresponding energy amount
     */
    function transferEnergyCredits(
        address _to,
        uint256 _amount,
        uint256 _energyAmount
    ) public nonReentrant returns (bool) {
        require(_amount > 0, "Amount must be positive");
        require(_energyAmount > 0, "Energy amount must be positive");
        require(balanceOf(msg.sender) >= _amount, "Insufficient credits");

        // Transfer credits
        _transfer(msg.sender, _to, _amount);

        // Update energy balances
        uint256 energyToTransfer = (_amount * 10**18) / energyToTokenRatio;
        require(energyBalance[msg.sender] >= energyToTransfer, "Insufficient energy balance");

        energyBalance[msg.sender] -= energyToTransfer;
        energyBalance[_to] += energyToTransfer;

        emit EnergyPurchased(_to, msg.sender, _amount, _energyAmount);
        return true;
    }

    /**
     * @dev Get energy balance for an address
     */
    function getEnergyBalance(address _user) public view returns (uint256) {
        return energyBalance[_user];
    }

    /**
     * @dev Convert energy amount to token amount
     */
    function energyToTokens(uint256 _energyAmount) public view returns (uint256) {
        return (_energyAmount * energyToTokenRatio) / 10**18;
    }

    /**
     * @dev Convert token amount to energy amount
     */
    function tokensToEnergy(uint256 _tokenAmount) public view returns (uint256) {
        return (_tokenAmount * 10**18) / energyToTokenRatio;
    }

    // Override required functions for Pausable
    function _update(
        address from,
        address to,
        uint256 value
    ) internal override whenNotPaused {
        super._update(from, to, value);
    }
}

contracts/EnergyTrading.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./EnergyCredits.sol";
import "./CarbonCredits.sol";

/**
 * @title EnergyTrading
 * @dev Main contract for managing energy trading in the PowerChain ecosystem
 * @notice This contract handles energy offers, purchases, and rewards distribution
 */
contract EnergyTrading is Ownable, Pausable, ReentrancyGuard {

    // Contract references
    EnergyCredits public energyCredits;
    CarbonCredits public carbonCredits;

    // Events
    event EnergyOfferCreated(uint256 indexed offerId, address indexed seller, uint256 energyAmount, uint256 pricePerKWh, string energyType);
    event EnergyOfferCancelled(uint256 indexed offerId, address indexed seller);
    event EnergyOfferUpdated(uint256 indexed offerId, uint256 newPrice, uint256 newAmount);
    event EnergyPurchased(uint256 indexed offerId, address indexed buyer, address indexed seller, uint256 energyAmount, uint256 totalCost);
    event EnergyDelivered(uint256 indexed transactionId, address indexed buyer, uint256 energyAmount);
    event TradingFeeUpdated(uint256 oldFee, uint256 newFee);

    // Structs
    struct EnergyOffer {
        uint256 id;
        address seller;
        uint256 energyAmount;     // in kWh (wei format)
        uint256 pricePerKWh;      // in Energy Credits per kWh
        string energyType;        // solar, wind, hydro, storage
        string location;
        uint256 timestamp;
        bool active;
        uint256 expiresAt;
    }

    struct EnergyTransaction {
        uint256 id;
        uint256 offerId;
        address buyer;
        address seller;
        uint256 energyAmount;
        uint256 totalCost;
        uint256 timestamp;
        TransactionStatus status;
        string deliveryDetails;
    }

    enum TransactionStatus {
        Pending,
        Confirmed,
        Delivered,
        Cancelled,
        Disputed
    }

    // State variables
    uint256 public nextOfferId = 1;
    uint256 public nextTransactionId = 1;
    uint256 public tradingFeeRate = 250; // 2.5% in basis points
    uint256 public constant MAX_TRADING_FEE = 1000; // 10% maximum

    mapping(uint256 => EnergyOffer) public energyOffers;
    mapping(uint256 => EnergyTransaction) public energyTransactions;
    mapping(address => uint256[]) public userOffers;
    mapping(address => uint256[]) public userTransactions;

    // Trading statistics
    mapping(address => uint256) public totalEnergyTraded;
    mapping(address => uint256) public totalTransactionCount;
    mapping(string => uint256) public energyTypeVolume;

    // Price tracking
    mapping(string => uint256) public currentMarketPrice;
    mapping(string => uint256) public priceUpdateTime;

    modifier validOffer(uint256 _offerId) {
        require(energyOffers[_offerId].active, "Offer not active");
        require(energyOffers[_offerId].expiresAt > block.timestamp, "Offer expired");
        _;
    }

    modifier onlySeller(uint256 _offerId) {
        require(energyOffers[_offerId].seller == msg.sender, "Not the seller");
        _;
    }

    constructor(address _energyCredits, address _carbonCredits) Ownable(msg.sender) {
        energyCredits = EnergyCredits(_energyCredits);
        carbonCredits = CarbonCredits(_carbonCredits);

        // Set initial market prices (in Energy Credits per kWh)
        currentMarketPrice["solar"] = 19 * 10**16;   // 0.19 EC per kWh
        currentMarketPrice["wind"] = 18 * 10**16;    // 0.18 EC per kWh
        currentMarketPrice["hydro"] = 17 * 10**16;   // 0.17 EC per kWh
        currentMarketPrice["storage"] = 20 * 10**16; // 0.20 EC per kWh
    }

    /**
     * @dev Create a new energy offer
     */
    function createEnergyOffer(
        uint256 _energyAmount,
        uint256 _pricePerKWh,
        string memory _energyType,
        string memory _location,
        uint256 _expiresInHours
    ) public whenNotPaused nonReentrant returns (uint256) {
        require(_energyAmount > 0, "Energy amount must be positive");
        require(_pricePerKWh > 0, "Price must be positive");
        require(_expiresInHours > 0 && _expiresInHours <= 168, "Expiry must be 1-168 hours");

        // Check if seller has enough energy to sell
        require(energyCredits.getEnergyBalance(msg.sender) >= _energyAmount, "Insufficient energy balance");

        uint256 offerId = nextOfferId++;
        uint256 expiresAt = block.timestamp + (_expiresInHours * 1 hours);

        energyOffers[offerId] = EnergyOffer({
            id: offerId,
            seller: msg.sender,
            energyAmount: _energyAmount,
            pricePerKWh: _pricePerKWh,
            energyType: _energyType,
            location: _location,
            timestamp: block.timestamp,
            active: true,
            expiresAt: expiresAt
        });

        userOffers[msg.sender].push(offerId);

        // Update market price
        updateMarketPrice(_energyType, _pricePerKWh);

        emit EnergyOfferCreated(offerId, msg.sender, _energyAmount, _pricePerKWh, _energyType);
        return offerId;
    }

    /**
     * @dev Purchase energy from an offer
     */
    function purchaseEnergy(uint256 _offerId, uint256 _energyAmount) 
        public 
        whenNotPaused 
        nonReentrant 
        validOffer(_offerId) 
        returns (uint256) {

        EnergyOffer storage offer = energyOffers[_offerId];
        require(offer.seller != msg.sender, "Cannot buy from yourself");
        require(_energyAmount > 0, "Energy amount must be positive");
        require(_energyAmount <= offer.energyAmount, "Not enough energy available");

        // Calculate costs
        uint256 totalCost = (_energyAmount * offer.pricePerKWh) / 10**18;
        uint256 tradingFee = (totalCost * tradingFeeRate) / 10000;
        uint256 sellerAmount = totalCost - tradingFee;

        // Check buyer has enough credits
        require(energyCredits.balanceOf(msg.sender) >= totalCost, "Insufficient credits");

        // Transfer energy credits
        require(energyCredits.transferFrom(msg.sender, offer.seller, sellerAmount), "Transfer to seller failed");

        // Transfer trading fee to contract owner
        if (tradingFee > 0) {
            require(energyCredits.transferFrom(msg.sender, owner(), tradingFee), "Fee transfer failed");
        }

        // Update energy balances - this function handles energy transfer internally
        // The energy will be transferred from seller to buyer via the trading mechanism

        // Issue carbon credits to buyer
        carbonCredits.issueCarbonCredits(msg.sender, _energyAmount, offer.energyType);

        // Create transaction record
        uint256 transactionId = nextTransactionId++;
        energyTransactions[transactionId] = EnergyTransaction({
            id: transactionId,
            offerId: _offerId,
            buyer: msg.sender,
            seller: offer.seller,
            energyAmount: _energyAmount,
            totalCost: totalCost,
            timestamp: block.timestamp,
            status: TransactionStatus.Confirmed,
            deliveryDetails: ""
        });

        userTransactions[msg.sender].push(transactionId);
        userTransactions[offer.seller].push(transactionId);

        // Update statistics
        totalEnergyTraded[msg.sender] += _energyAmount;
        totalEnergyTraded[offer.seller] += _energyAmount;
        totalTransactionCount[msg.sender]++;
        totalTransactionCount[offer.seller]++;
        energyTypeVolume[offer.energyType] += _energyAmount;

        // Update offer
        offer.energyAmount -= _energyAmount;
        if (offer.energyAmount == 0) {
            offer.active = false;
        }

        emit EnergyPurchased(_offerId, msg.sender, offer.seller, _energyAmount, totalCost);
        return transactionId;
    }

    /**
     * @dev Cancel an energy offer
     */
    function cancelEnergyOffer(uint256 _offerId) 
        public 
        whenNotPaused 
        validOffer(_offerId) 
        onlySeller(_offerId) {

        energyOffers[_offerId].active = false;
        emit EnergyOfferCancelled(_offerId, msg.sender);
    }

    /**
     * @dev Update energy offer price and amount
     */
    function updateEnergyOffer(uint256 _offerId, uint256 _newPrice, uint256 _newAmount) 
        public 
        whenNotPaused 
        validOffer(_offerId) 
        onlySeller(_offerId) {

        require(_newPrice > 0, "Price must be positive");
        require(_newAmount > 0, "Amount must be positive");

        EnergyOffer storage offer = energyOffers[_offerId];
        require(energyCredits.getEnergyBalance(msg.sender) >= _newAmount, "Insufficient energy balance");

        offer.pricePerKWh = _newPrice;
        offer.energyAmount = _newAmount;

        updateMarketPrice(offer.energyType, _newPrice);

        emit EnergyOfferUpdated(_offerId, _newPrice, _newAmount);
    }

    /**
     * @dev Mark energy as delivered
     */
    function markEnergyDelivered(uint256 _transactionId, string memory _deliveryDetails) 
        public 
        whenNotPaused {

        EnergyTransaction storage transaction = energyTransactions[_transactionId];
        require(transaction.seller == msg.sender, "Not the seller");
        require(transaction.status == TransactionStatus.Confirmed, "Invalid transaction status");

        transaction.status = TransactionStatus.Delivered;
        transaction.deliveryDetails = _deliveryDetails;

        emit EnergyDelivered(_transactionId, transaction.buyer, transaction.energyAmount);
    }

    /**
     * @dev Update market price for energy type
     */
    function updateMarketPrice(string memory _energyType, uint256 _price) internal {
        // Simple price update - in production, this would use a more sophisticated algorithm
        currentMarketPrice[_energyType] = _price;
        priceUpdateTime[_energyType] = block.timestamp;
    }

    /**
     * @dev Set trading fee rate (only owner)
     */
    function setTradingFeeRate(uint256 _newRate) public onlyOwner {
        require(_newRate <= MAX_TRADING_FEE, "Fee rate too high");
        uint256 oldRate = tradingFeeRate;
        tradingFeeRate = _newRate;
        emit TradingFeeUpdated(oldRate, _newRate);
    }

    /**
     * @dev Get active offers for an energy type
     */
    function getActiveOffers(string memory _energyType) public view returns (uint256[] memory) {
        uint256[] memory activeOffers = new uint256[](100); // Temporary array
        uint256 count = 0;

        for (uint256 i = 1; i < nextOfferId; i++) {
            EnergyOffer storage offer = energyOffers[i];
            if (offer.active && 
                offer.expiresAt > block.timestamp && 
                keccak256(bytes(offer.energyType)) == keccak256(bytes(_energyType))) {
                activeOffers[count] = i;
                count++;
            }
        }

        // Resize array to actual count
        uint256[] memory result = new uint256[](count);
        for (uint256 i = 0; i < count; i++) {
            result[i] = activeOffers[i];
        }

        return result;
    }

    /**
     * @dev Get user's offers
     */
    function getUserOffers(address _user) public view returns (uint256[] memory) {
        return userOffers[_user];
    }

    /**
     * @dev Get user's transactions
     */
    function getUserTransactions(address _user) public view returns (uint256[] memory) {
        return userTransactions[_user];
    }

    /**
     * @dev Get market statistics
     */
    function getMarketStats() public view returns (
        uint256 totalActiveOffers,
        uint256 totalTransactions,
        uint256 totalVolumeTraded
    ) {
        uint256 activeOffers = 0;
        uint256 volumeTraded = 0;

        for (uint256 i = 1; i < nextOfferId; i++) {
            if (energyOffers[i].active && energyOffers[i].expiresAt > block.timestamp) {
                activeOffers++;
            }
        }

        for (uint256 i = 1; i < nextTransactionId; i++) {
            volumeTraded += energyTransactions[i].energyAmount;
        }

        return (activeOffers, nextTransactionId - 1, volumeTraded);
    }

    /**
     * @dev Get current market price for energy type
     */
    function getMarketPrice(string memory _energyType) public view returns (uint256, uint256) {
        return (currentMarketPrice[_energyType], priceUpdateTime[_energyType]);
    }

    /**
     * @dev Pause the contract (only owner)
     */
    function pause() public onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause the contract (only owner)
     */
    function unpause() public onlyOwner {
        _unpause();
    }

    /**
     * @dev Emergency withdraw (only owner)
     */
    function emergencyWithdraw() public onlyOwner {
        uint256 balance = energyCredits.balanceOf(address(this));
        if (balance > 0) {
            energyCredits.transfer(owner(), balance);
        }
    }
}

contracts/GovernanceDAO.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title GovernanceDAO
 * @dev DAO governance contract for PowerChain community decision making
 * @notice This contract manages governance tokens and voting on platform proposals
 */
contract GovernanceDAO is ERC20, ERC20Burnable, Ownable, Pausable, ReentrancyGuard {

    // Events
    event ProposalCreated(uint256 indexed proposalId, address indexed proposer, string title, string description);
    event VoteCast(uint256 indexed proposalId, address indexed voter, bool support, uint256 weight);
    event ProposalExecuted(uint256 indexed proposalId, bool successful);
    event ProposalCancelled(uint256 indexed proposalId);
    event GovernanceTokensIssued(address indexed to, uint256 amount, string reason);
    event DelegatePowerChanged(address indexed delegator, address indexed oldDelegate, address indexed newDelegate);

    // Structs
    struct Proposal {
        uint256 id;
        address proposer;
        string title;
        string description;
        uint256 startTime;
        uint256 endTime;
        uint256 forVotes;
        uint256 againstVotes;
        uint256 abstainVotes;
        bool executed;
        bool cancelled;
        ProposalType proposalType;
        bytes executionData;
    }

    enum ProposalType {
        General,
        TradingFeeChange,
        TokenMinting,
        ContractUpgrade,
        ParameterChange,
        Emergency
    }

    enum VoteType {
        Against,
        For,
        Abstain
    }

    // Constants
    uint256 public constant INITIAL_SUPPLY = 1000000 * 10**18; // 1 million GT
    uint256 public constant MAX_SUPPLY = 10000000 * 10**18; // 10 million GT max
    uint256 public constant VOTING_PERIOD = 7 days;
    uint256 public constant VOTING_DELAY = 1 days;
    uint256 public constant PROPOSAL_THRESHOLD = 1000 * 10**18; // 1000 GT to create proposal
    uint256 public constant QUORUM_VOTES = 50000 * 10**18; // 50000 GT for quorum

    // State variables
    uint256 public nextProposalId = 1;
    uint256 public totalProposals = 0;
    uint256 public totalVotesCast = 0;

    mapping(uint256 => Proposal) public proposals;
    mapping(uint256 => mapping(address => bool)) public hasVoted;
    mapping(uint256 => mapping(address => VoteType)) public votes;
    mapping(address => address) public delegates;
    mapping(address => uint256) public votingPower;
    mapping(address => uint256) public delegatedPower;

    // Authorized addresses for token minting
    mapping(address => bool) public authorizedMinters;

    // Proposal execution targets
    mapping(uint256 => address) public executionTargets;

    modifier onlyAuthorizedMinter() {
        require(authorizedMinters[msg.sender] || msg.sender == owner(), "Not authorized to mint");
        _;
    }

    modifier validProposal(uint256 _proposalId) {
        require(_proposalId > 0 && _proposalId < nextProposalId, "Invalid proposal ID");
        require(!proposals[_proposalId].cancelled, "Proposal cancelled");
        _;
    }

    modifier votingActive(uint256 _proposalId) {
        Proposal storage proposal = proposals[_proposalId];
        require(block.timestamp >= proposal.startTime, "Voting not started");
        require(block.timestamp <= proposal.endTime, "Voting ended");
        _;
    }

    constructor() ERC20("PowerChain Governance Token", "GT") Ownable(msg.sender) {
        _mint(msg.sender, INITIAL_SUPPLY);
        authorizedMinters[msg.sender] = true;

        // Set initial voting power
        votingPower[msg.sender] = INITIAL_SUPPLY;
    }

    /**
     * @dev Create a new governance proposal
     */
    function createProposal(
        string memory _title,
        string memory _description,
        ProposalType _proposalType,
        address _executionTarget,
        bytes memory _executionData
    ) public whenNotPaused nonReentrant returns (uint256) {
        require(balanceOf(msg.sender) >= PROPOSAL_THRESHOLD, "Insufficient tokens to create proposal");
        require(bytes(_title).length > 0, "Title cannot be empty");
        require(bytes(_description).length > 0, "Description cannot be empty");

        uint256 proposalId = nextProposalId++;
        uint256 startTime = block.timestamp + VOTING_DELAY;
        uint256 endTime = startTime + VOTING_PERIOD;

        proposals[proposalId] = Proposal({
            id: proposalId,
            proposer: msg.sender,
            title: _title,
            description: _description,
            startTime: startTime,
            endTime: endTime,
            forVotes: 0,
            againstVotes: 0,
            abstainVotes: 0,
            executed: false,
            cancelled: false,
            proposalType: _proposalType,
            executionData: _executionData
        });

        executionTargets[proposalId] = _executionTarget;
        totalProposals++;

        emit ProposalCreated(proposalId, msg.sender, _title, _description);
        return proposalId;
    }

    /**
     * @dev Cast a vote on a proposal
     */
    function castVote(uint256 _proposalId, VoteType _voteType) 
        public 
        whenNotPaused 
        nonReentrant 
        validProposal(_proposalId) 
        votingActive(_proposalId) {

        require(!hasVoted[_proposalId][msg.sender], "Already voted");

        uint256 weight = getVotingPower(msg.sender);
        require(weight > 0, "No voting power");

        Proposal storage proposal = proposals[_proposalId];

        if (_voteType == VoteType.For) {
            proposal.forVotes += weight;
        } else if (_voteType == VoteType.Against) {
            proposal.againstVotes += weight;
        } else {
            proposal.abstainVotes += weight;
        }

        hasVoted[_proposalId][msg.sender] = true;
        votes[_proposalId][msg.sender] = _voteType;
        totalVotesCast++;

        emit VoteCast(_proposalId, msg.sender, _voteType == VoteType.For, weight);
    }

    /**
     * @dev Execute a proposal if it has passed
     */
    function executeProposal(uint256 _proposalId) 
        public 
        whenNotPaused 
        nonReentrant 
        validProposal(_proposalId) {

        Proposal storage proposal = proposals[_proposalId];
        require(block.timestamp > proposal.endTime, "Voting still active");
        require(!proposal.executed, "Already executed");

        // Check if proposal passed
        bool passed = hasProposalPassed(_proposalId);

        if (passed && executionTargets[_proposalId] != address(0)) {
            // Execute the proposal
            (bool success, ) = executionTargets[_proposalId].call(proposal.executionData);
            proposal.executed = true;
            emit ProposalExecuted(_proposalId, success);
        } else {
            proposal.executed = true;
            emit ProposalExecuted(_proposalId, false);
        }
    }

    /**
     * @dev Cancel a proposal (only proposer or owner)
     */
    function cancelProposal(uint256 _proposalId) 
        public 
        whenNotPaused 
        validProposal(_proposalId) {

        Proposal storage proposal = proposals[_proposalId];
        require(msg.sender == proposal.proposer || msg.sender == owner(), "Not authorized");
        require(!proposal.executed, "Already executed");
        require(block.timestamp < proposal.endTime, "Voting ended");

        proposal.cancelled = true;
        emit ProposalCancelled(_proposalId);
    }

    /**
     * @dev Delegate voting power to another address
     */
    function delegate(address _delegatee) public whenNotPaused {
        address oldDelegate = delegates[msg.sender];
        delegates[msg.sender] = _delegatee;

        uint256 userBalance = balanceOf(msg.sender);

        // Remove from old delegate
        if (oldDelegate != address(0)) {
            delegatedPower[oldDelegate] -= userBalance;
        }

        // Add to new delegate
        if (_delegatee != address(0)) {
            delegatedPower[_delegatee] += userBalance;
        }

        // Update voting power
        updateVotingPower(msg.sender);
        if (oldDelegate != address(0)) {
            updateVotingPower(oldDelegate);
        }
        if (_delegatee != address(0)) {
            updateVotingPower(_delegatee);
        }

        emit DelegatePowerChanged(msg.sender, oldDelegate, _delegatee);
    }

    /**
     * @dev Issue governance tokens (only authorized minters)
     */
    function issueGovernanceTokens(
        address _to,
        uint256 _amount,
        string memory _reason
    ) public onlyAuthorizedMinter nonReentrant {
        require(_amount > 0, "Amount must be positive");
        require(totalSupply() + _amount <= MAX_SUPPLY, "Would exceed max supply");

        _mint(_to, _amount);
        updateVotingPower(_to);

        emit GovernanceTokensIssued(_to, _amount, _reason);
    }

    /**
     * @dev Update voting power for an address
     */
    function updateVotingPower(address _user) internal {
        uint256 balance = balanceOf(_user);
        uint256 delegated = delegatedPower[_user];

        if (delegates[_user] != address(0)) {
            // User has delegated their tokens
            votingPower[_user] = delegated;
        } else {
            // User votes with their own tokens + delegated tokens
            votingPower[_user] = balance + delegated;
        }
    }

    /**
     * @dev Get voting power for an address
     */
    function getVotingPower(address _user) public view returns (uint256) {
        return votingPower[_user];
    }

    /**
     * @dev Check if a proposal has passed
     */
    function hasProposalPassed(uint256 _proposalId) public view returns (bool) {
        Proposal storage proposal = proposals[_proposalId];

        uint256 totalVotes = proposal.forVotes + proposal.againstVotes + proposal.abstainVotes;

        // Check quorum
        if (totalVotes < QUORUM_VOTES) {
            return false;
        }

        // Check if more for votes than against
        return proposal.forVotes > proposal.againstVotes;
    }

    /**
     * @dev Get proposal details
     */
    function getProposal(uint256 _proposalId) public view returns (
        uint256 id,
        address proposer,
        string memory title,
        string memory description,
        uint256 startTime,
        uint256 endTime,
        uint256 forVotes,
        uint256 againstVotes,
        uint256 abstainVotes,
        bool executed,
        bool cancelled,
        ProposalType proposalType
    ) {
        Proposal storage proposal = proposals[_proposalId];
        return (
            proposal.id,
            proposal.proposer,
            proposal.title,
            proposal.description,
            proposal.startTime,
            proposal.endTime,
            proposal.forVotes,
            proposal.againstVotes,
            proposal.abstainVotes,
            proposal.executed,
            proposal.cancelled,
            proposal.proposalType
        );
    }

    /**
     * @dev Get voting statistics
     */
    function getVotingStats() public view returns (
        uint256 totalProposalsCount,
        uint256 totalVotesCastCount,
        uint256 activeProposals,
        uint256 executedProposals
    ) {
        uint256 active = 0;
        uint256 executed = 0;

        for (uint256 i = 1; i < nextProposalId; i++) {
            if (!proposals[i].cancelled) {
                if (proposals[i].executed) {
                    executed++;
                } else if (block.timestamp <= proposals[i].endTime) {
                    active++;
                }
            }
        }

        return (totalProposals, totalVotesCast, active, executed);
    }

    /**
     * @dev Add authorized minter
     */
    function addMinter(address _minter) public onlyOwner {
        authorizedMinters[_minter] = true;
    }

    /**
     * @dev Remove authorized minter
     */
    function removeMinter(address _minter) public onlyOwner {
        authorizedMinters[_minter] = false;
    }

    /**
     * @dev Pause the contract (only owner)
     */
    function pause() public onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause the contract (only owner)
     */
    function unpause() public onlyOwner {
        _unpause();
    }

    // Override transfer functions to update voting power
    function transfer(address to, uint256 amount) public override returns (bool) {
        bool success = super.transfer(to, amount);
        if (success) {
            updateVotingPower(msg.sender);
            updateVotingPower(to);
        }
        return success;
    }

    function transferFrom(address from, address to, uint256 amount) public override returns (bool) {
        bool success = super.transferFrom(from, to, amount);
        if (success) {
            updateVotingPower(from);
            updateVotingPower(to);
        }
        return success;
    }

    // Override required functions for Pausable
    function _update(
        address from,
        address to,
        uint256 value
    ) internal override whenNotPaused {
        super._update(from, to, value);
    }
}

